def main():
    num_us = 75
    num_ca = 25
    
    fh = open('addresses.txt', 'r')
    lines = fh.readlines()
    for i in range(0, len(lines), 2):
        line1 = lines[i].strip()
        line2 = ""
        a = lines[i+1].strip().split(',')
        city = a[0]
        b = a[1].strip().split(' ')
        state = b[0]
        zipcode = " ".join(b[1:])
        country = None
        country_code = None
        if i/2 < num_us:
            country = 'US'
            country_code = '209'
        else:
            country = 'CA'
            country_code = '79'
        data_tuple = (line1, line2, city, state, zipcode, country, country_code)
        print(data_tuple)

if __name__ == '__main__':
    main()